    font-weight: 700;
    font-size: 64px;
    text-align:center;
    margin-bottom:18px;
    color:#5D5D5D;
`

export const TitleCard1 = styled.div`
    font-size:36px;
    color:#5D5D5D;


`

export const DescriptionCard1 = styled.div`
    font-size:24px;
    color:#5F5F5F;
    margin-bottom:15px;
`

export const LeftCard1 = styled.div`
border-right: 1px solid #ccc;
width:50%;
`

export const RightCard1 = styled.div``

