    font-size:24px;
    color:#5F5F5F;
    margin-bottom:15px;
`

export const LeftCard1 = styled.div`
border-right: 1px solid #ccc;
width:50%;
`

export const RightCard1 = styled.div``

export const Button = styled.div`
    display:grid;
    place-items:center;
    font-weight:700px;
